mapper.select:
SELECT

FROM sys_config a
WHERE a.id = #{id}

SELECT FROM sys_config a
and a.sys_name like '%'||#{page.key}||'%') '%'+#{page.key}+'%') CONCAT('%',#{page.key},'%') OR a.module_name like '%'||#{page.key}||'%') '%'+#{page.key}+'%') CONCAT('%',#{page.key},'%') OR a.config_name like '%'||#{page.key}||'%') '%'+#{page.key}+'%') CONCAT('%',#{page.key},'%')
ORDER BY ${page.orderBy} LIMIT ${page.pageNo*page.pageSize},${page.pageSize}
